package Jan21st_Lectur19th_BitMasking;

public class To_find_odd_or_evenbybitmasking {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int n=15;
  if((n&1)==0)
	  System.out.println("even");
  else
	  System.out.println("odd");
	}
	/* and is to be used to check final digit -if 1 at the end , number is odd
	 * bitmasking is 2-3 times faster than normal code 
	 */

}
