public class max_a_b_c {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a=2;
    int b=7;
    int c=9;
    if (a>b && a>c)
    	System.out.println("greatest is a="+a);
    else if(b>a&& b>c)
    	System.out.println("greatest is b="+b);
    else 
    	System.out.println("greatest is c=" + c);
	}

}
