package lecture3_javacrux2022_Pattern;
import java.util.*;

public class Pattern_0 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc =new Scanner(System.in);
		int n =sc.nextInt();
		int i=1;
		while(i<=n)
		{
			System.out.print("* ");
			i++;
		}

	}

}
